targets.expr:
kube_pod_container_info{ namespace="$namespace", pod=~"$pod", container="$container"}
* on(pod)
group_left(workload) namespace_workload_pod:kube_pod_owner:relabel{ namespace="$namespace", workload="$job" }

kube_deployment_status_replicas_unavailable{ namespace="$namespace", deployment=~"$job" } or
(kube_statefulset_status_replicas{ namespace="$namespace", statefulset=~"$job"}
- kube_statefulset_status_replicas_available{ namespace="$namespace", statefulset=~"$job"})

sum(
    node_namespace_pod_container:container_cpu_usage_seconds_total:sum_irate{namespace="$namespace", pod=~"$pod", container="$container" }
  * on(pod)
    group_left(workload) namespace_workload_pod:kube_pod_owner:relabel{ namespace="$namespace", workload="$job" }
) by (pod)
